import React, { useState } from 'react';
import { authenticate } from '../Fetch/auth';
import { useNavigate } from 'react-router-dom';
function Login() {
    const [username, setUsername] = useState('');
    const [password, setPassword] = useState('');
    const [error, setError] = useState('');
    const [success, setSuccess] = useState('');

    const navigate = useNavigate();

    const handleSubmit = async event => {
        event.preventDefault();
        try {
            const response = await authenticate(username, password);
            console.log(response); // Handle the authentication response here
            // Navigate to another screen and pass the response data
            navigate('/planificador', { responseData: response });
        } catch (error) {
            console.error(error); // Handle authentication errors here
            setError('Validar datos'); // Set the error message in case of authentication failure
            setSuccess(''); // Clear the success message in case of authentication failure
        }
    };

    return (
        <div className="max-w-md w-full space-y-8 bg-white rounded-lg shadow-md">
            <div>
                <h2 className="mt-6 text-center text-3xl font-extrabold text-gray-900">
                    Planificador de tareas
                </h2>
            </div>
            <form className="mt-8 space-y-6" onSubmit={handleSubmit}>
                <input type="hidden" name="remember" value="true" />
                <div className="rounded-md shadow-sm -space-y-px mt-0">
                    <div>
                        <label htmlFor="username" className="sr-only">Nombre de usuario</label>
                        <input id="username" name="username" type="text" required className="appearance-none rounded-none relative block w-full px-3 py-2 border border-gray-300 placeholder-gray-500 text-gray-900 rounded-t-md focus:outline-none focus:ring-indigo-500 focus:border-indigo-500 focus:z-10 sm:text-sm" placeholder="Nombre de usuario" value={username} onChange={e => setUsername(e.target.value)} />
                    </div>
                    <div>
                        <label htmlFor="password" className="sr-only">Contraseña</label>
                        <input id="password" name="password" type="password" required className="appearance-none rounded-none relative block w-full px-3 py-2 border border-gray-300 placeholder-gray-500 text-gray-900 rounded-b-md focus:outline-none focus:ring-indigo-500 focus:border-indigo-500 focus:z-10 sm:text-sm" placeholder="Contraseña" value={password} onChange={e => setPassword(e.target.value)} />
                    </div>
                </div>

                {error && <p className="text-red-500">{error}</p>} {/* Muestra el mensaje de error si existe */}
                {success && <p className="text-green-500">{success}</p>} {/* Muestra el mensaje de éxito si existe */}

                <div>
                    <button type="submit" className="group relative w-full flex justify-center py-2 px-4 border border-transparent text-sm font-medium rounded-md text-white bg-indigo-600 hover:bg-indigo-700 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-indigo-500">
                        Iniciar sesión
                    </button>
                </div>
            </form>
        </div>
    );
}

export default Login;
